const readline = require('readline');
const rl = readline.createInterface({
  input: process.stdin,
  output: process.stdout
});

let playerName = '';

function startGame() {
  console.log('Welkom bij Timmy\'s avontuur!');
  rl.question('Voer je naam in: ', (name) => {
    playerName = name;
    console.log(`Welkom, ${playerName}! Timmy gaat op avontuur.`);
    choosePath();
  });
}

function choosePath() {
  console.log(`Je staat voor een splitsing in het pad, ${playerName}.`);
  rl.question('A. Neem het linkerpad of B. Neem het rechterpad: ', (choice) => {
    if (choice.toLowerCase() === 'a') {
      crossBridge();
    } else if (choice.toLowerCase() === 'b') {
      swimRiver();
    } else {
      console.log('Ongeldige keuze. Probeer opnieuw.');
      choosePath();
    }
  });
}

function crossBridge() {
  console.log('Je komt een brug tegen.');
  rl.question('A. Steek de brug over of B. Zwem door de rivier: ', (choice) => {
    if (choice.toLowerCase() === 'a') {
      meetWizard();
    } else if (choice.toLowerCase() === 'b') {
      console.log('Je verdrinkt in de rivier. Einde 2: Game over.');
      rl.close();
    } else {
      console.log('Ongeldige keuze. Probeer opnieuw.');
      crossBridge();
    }
  });
}

function swimRiver() {
  console.log('Je zwemt door de rivier en komt een tovenaar tegen.');
  rl.question('A. Vraag om hulp of B. Ga verder zonder hulp: ', (choice) => {
    if (choice.toLowerCase() === 'a') {
      askWizardForHelp();
    } else if (choice.toLowerCase() === 'b') {
      goBack();
    } else {
      console.log('Ongeldige keuze. Probeer opnieuw.');
      swimRiver();
    }
  });
}

function meetWizard() {
  console.log('Je komt een tovenaar tegen.');
  rl.question('A. Vraag om hulp of B. Ga verder zonder hulp: ', (choice) => {
    if (choice.toLowerCase() === 'a') {
      askWizardForHelp();
    } else if (choice.toLowerCase() === 'b') {
      goBack();
    } else {
      console.log('Ongeldige keuze. Probeer opnieuw.');
      meetWizard();
    }
  });
}

function askWizardForHelp() {
  console.log('De tovenaar biedt je hulp aan, maar hij wil een sleutel in ruil.');
  rl.question('A. Vraag de tovenaar om de sleutel of B. Ga terug: ', (choice) => {
    if (choice.toLowerCase() === 'a') {
      console.log(`Einde 3: Je hebt de sleutel en gaat verder, ${playerName}!`);
      rl.close();
    } else if (choice.toLowerCase() === 'b') {
      goBack();
    } else {
      console.log('Ongeldige keuze. Probeer opnieuw.');
      askWizardForHelp();
    }
  });
}

function goBack() {
  console.log(`Je gaat terug, ${playerName}. Einde 4: Je avontuur eindigt.`);
  rl.close();
}

startGame();